    private void jbLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbLoginActionPerformed
        try {
            String user;
            user = jtUsuario.getText();
            String senha = new String(jtSenha.getPassword());
            
            UsuarioDTO uDTO = new UsuarioDTO();
            
            uDTO.setUsuario(user);
            uDTO.setSenha(senha);
            
            UsuarioDAO uDAO = new UsuarioDAO();
            ResultSet rsDAO = uDAO.autentica(uDTO);
            
            if(rsDAO.next()){
                jfPrincipal fmp = new jfPrincipal();
                fmp.setVisible(true);
                
                dispose();
            }else{
                JOptionPane.showMessageDialog(null, "NÃ£o logado");
            }
            
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "ERRO NA TELA DE LOGIN)"+e.getMessage());
        }
    }//GEN-LAST:event_jbLoginActionPerformed

